package com.mrpowergamerbr.loritta.utils.locale

import com.mrpowergamerbr.loritta.utils.f
import mu.KotlinLogging

/**
 * Localization class, this is partly generated by the LocaleGenerator
 */
open class BaseLocale {
	companion object {
		@JvmStatic
		private val logger = KotlinLogging.logger {}
	}

	@Transient
	@Deprecated("Please use the inner classes")
	var strings = mutableMapOf<String, String>()
	@Transient
	var commands = Commands()
	@Transient
	var loritta = Loritta()

	@Deprecated("Please use the inner classes")
	operator fun get(key: String, vararg arguments: Any?): String {
		if (!strings.containsKey(key)) {
			logger.warn {"Missing translation key! $key" }
			return key
		}
		return strings[key]!!.f(*arguments)
	}

	// Generic
	lateinit var SHIP_valor90: List<String>

	lateinit var SHIP_valor80: List<String>

	lateinit var SHIP_valor70: List<String>

	lateinit var SHIP_valor60: List<String>

	lateinit var SHIP_valor50: List<String>

	lateinit var SHIP_valor40: List<String>

	lateinit var SHIP_valor30: List<String>

	lateinit var SHIP_valor20: List<String>

	lateinit var SHIP_valor10: List<String>

	lateinit var SHIP_valor0: List<String>

	class Loritta {
		lateinit var translationAuthors: List<LocaleMessage>
	}
	class Commands {
		lateinit var pleaseWaitCooldown: LocaleMessage
		lateinit var errorWhileExecutingCommand: LocaleMessage
		lateinit var cantUseInPrivate: LocaleMessage
		lateinit var userDoesNotExists: LocaleMessage
		class Arguments {
			lateinit var text: LocaleMessage
			lateinit var number: LocaleMessage
			lateinit var user: LocaleMessage
			lateinit var image: LocaleMessage
		}
		var arguments = Arguments()

		class Ajuda {
			lateinit var errorWhileOpeningDm: LocaleMessage
		}
		var ajuda = Ajuda()

		class Roll {
			lateinit var description: LocaleMessage
			lateinit var howMuchSides: LocaleMessage
		}
		var roll = Roll()

		class Vieirinha {
			lateinit var description: LocaleMessage
			lateinit var examples: List<LocaleMessage>
			lateinit var responses: List<LocaleMessage>
		}
		var vieirinha = Vieirinha()

		class Actions {
			lateinit var examples: List<LocaleMessage>
			class Slap {
				lateinit var description: LocaleMessage
				lateinit var response: LocaleMessage
				lateinit var responseAntiIdiot: LocaleMessage
			}
			var slap = Slap()

			class Kiss {
				lateinit var description: LocaleMessage
				lateinit var response: LocaleMessage
				lateinit var responseAntiIdiot: LocaleMessage
			}
			var kiss = Kiss()

			class Attack {
				lateinit var description: LocaleMessage
				lateinit var response: LocaleMessage
				lateinit var responseAntiIdiot: LocaleMessage
			}
			var attack = Attack()

			class Hug {
				lateinit var description: LocaleMessage
				lateinit var response: LocaleMessage
			}
			var hug = Hug()

			class Dance {
				lateinit var description: LocaleMessage
				lateinit var response: LocaleMessage
			}
			var dance = Dance()

		}
		var actions = Actions()
	}
}